$("document").ready(function () {
        menu_open()
		menu_close()
		lnb()
})
		


        function menu_open(){
            $("#sidebarToggle, #sidebarToggleTop").click(function () {
                $(".kt-aside").css("left", "0")
            })
        }


		function menu_close(){
			if(matchMedia("screen and (max-width:800px)").matches){
				$(".kt-container, #ctt_con").click(function(){
				$(".kt-aside").css("left", "-100%")	
			})
			}else{
				$(".kt-aside").css("display", "block")
				}
		}

		function lnb(){
		$(".kt-menu__nav > li:nth-child(4),.kt-menu__nav > li:nth-child(5)").mouseover(function(){
			$(this).children("div.kt-menu__submenu").stop().slideDown()
		})
		$(".kt-menu__nav > li:nth-child(4),.kt-menu__nav > li:nth-child(5)").mouseout(function(){
			$(this).children("div.kt-menu__submenu").stop().slideUp()
		})
		
		}